node.js:
function firstAndLast() {
 if (arguments.length >= 1) {
  console.log('first: ' + arguments[0]);
 }
 if (arguments.length >= 2) {
  console.log('last: ' + arguments[1]);
 }
}

// ... operator new in ES6:
function firstAndLast(...a) {
 if (a.length >= 1) {
  console.log('first: ' + a[0]);
 }
 if (a.length >= 2) {
  console.log('last: ' + a[1]);
 }
}

