node.js:
function * makeCounter () {
 let i = 0;
 while (true) {
  yield ++i;
 }
}

let nays = makeCounter();
for (let cnt of nays) {
 console.log(cnt);
 if (cnt > 100) {
  break;
 }
}

php:
# PHP 5.5:
function make_counter() {
 $i = 0;
 while (1) {
  yield ++$i;
 }
}

$nays = make_counter();
# does not return a value:
$nays->next();
# runs generator if generator has not
# yet yielded:
echo $nays->current();

python:
def logcall(f):
 def wrapper(*a, **opts):
  print('calling ' + f.__name__)
  f(*a, **opts)
  print('called ' + f.__name__)
 return wrapper

@logcall
def square(x):
 return x * x

